CREATE TABLE bans ( i INTEGER NOT NULL AUTO_INCREMENT PRIMARY KEY, channel VARCHAR(512), setter VARCHAR(512), mask VARCHAR(512), isSet BOOL, setDate DATETIME, reason VARCHAR(512), action INTEGER, actionDate DATETIME, `affected` TEXT, unbanner VARCHAR(512), unbanDate DATETIME, type VARCHAR(20), nagged INTEGER);
CREATE TABLE log (i INTEGER NOT NULL AUTO_INCREMENT PRIMARY KEY, channel VARCHAR(512), sender VARCHAR(512), command  VARCHAR(512), data VARCHAR(512), date DATETIME);
